import React, { useState } from "react";
import styled from "styled-components";
import logo from "../image/logo_1.png";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Import Axios pour les requêtes HTTP

// Styles avec styled-components
const LoginContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  padding: 20px;
  background-color: var(--background-color);
  background-image: url("../image/testdefond.jpg");
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  background-attachment: fixed;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 650px;
  background-color: rgb(111, 107, 107);
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  box-sizing: border-box;
`;

const LogoContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
`;

const LoginLogo = styled.img`
  width: 80px;
  cursor: pointer;
`;

const LoginTitle = styled.h2`
  text-align: center;
  font-size: 24px;
  margin-bottom: 20px;
`;

const InputContainer = styled.div`
  margin-bottom: 15px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 16px;
  box-sizing: border-box;
`;

const SubmitButton = styled.button`
  padding: 10px;
  font-size: 16px;
  background-color: black;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;
  margin-bottom: 20px;

  &:hover {
    background-color: #333;
  }
`;

const FormLinks = styled.div`
  text-align: center;
  font-size: 14px;
`;

const Link = styled.a`
  color: black;
  text-decoration: none;

  &:hover {
    text-decoration: underline;
  }
`;

const Divider = styled.span`
  margin: 0 10px;
  color: #666;
`;

const ThemeToggle = styled.label`
  position: absolute;
  top: 20px;
  right: 20px;
  cursor: pointer;
`;

const Login = () => {
  const [email, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [theme, setTheme] = useState("light");

  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    document.documentElement.setAttribute("data-theme", newTheme);
  };

  const navigate = useNavigate();

  const handleHomeClick = () => {
    navigate("/home");
  };

  // Fonction pour gérer la connexion
  const handleLogin = async (e) => {
    e.preventDefault();

    // Validation basique des champs
    if (!email || !password) {
      setErrorMessage("Please fill in all fields.");
      return;
    }

    try {
      // Requête vers le backend pour la connexion
      const response = await axios.post("/api/users/signin", {
        email: email,
        password: password,
      });

      if (response.data.accessToken) {
        // Sauvegarder le token JWT dans sessionStorage
        sessionStorage.setItem("token", response.data.accessToken);

        // Rediriger vers la page d'accueil ou une autre page
        navigate("/home");
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setErrorMessage("Invalid username or password.");
      } else {
        setErrorMessage("Something went wrong. Please try again later.");
      }
      console.error("Login error:", error);
    }
  };

  return (
    <LoginContainer>
      <ThemeToggle className="swap swap-rotate">
        <input type="checkbox" onChange={toggleTheme} />
      </ThemeToggle>

      <Form onSubmit={handleLogin}>
        <LogoContainer>
          <LoginLogo src={logo} alt="Logo Galaxia" onClick={handleHomeClick} />
        </LogoContainer>
        <LoginTitle>Login</LoginTitle>
        {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        <InputContainer>
          <Input
            type="text"
            placeholder="Username or Email"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </InputContainer>
        <InputContainer>
          <Input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </InputContainer>
        <SubmitButton type="submit">Login</SubmitButton>
        <FormLinks>
          <Link href="/forgot_password">Forgot your password?</Link>
          <Divider>|</Divider>
          <Link href="/api/users/signup">No account? Sign up</Link>
        </FormLinks>
      </Form>
    </LoginContainer>
  );
};

export default Login;
