import React, { useState } from "react";
import styled from "styled-components";
import logo from "../image/logo_1.png";
import { useNavigate } from "react-router-dom";
import { useAuth } from "./AuthContext"; // Modifiez le chemin selon votre structure de projet
// Styles avec styled-components
const LoginContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  padding: 20px;
  background-color: var(--background-color);
  background-image: url("../image/testdefond.jpg");
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  background-attachment: fixed;
`;
const Form = styled.form`
  display: flex;
  flex-direction: column;
  width: 650px;
  background-color: rgb(111, 107, 107);
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  box-sizing: border-box;
`;
const LogoContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
`;
const LoginLogo = styled.img`
  width: 80px;
  cursor: pointer;
`;
const LoginTitle = styled.h2`
  text-align: center;
  font-size: 24px;
  margin-bottom: 20px;
`;
const InputContainer = styled.div`
  margin-bottom: 15px;
`;
const Input = styled.input`
  width: 100%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 16px;
  box-sizing: border-box;
`;
const SubmitButton = styled.button`
  padding: 10px;
  font-size: 16px;
  background-color: black;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s ease;
  margin-bottom: 20px;
  &:hover {
    background-color: #333;
  }
`;
const FormLinks = styled.div`
  text-align: center;
  font-size: 14px;
`;
const Link = styled.a`
  color: black;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;
const Divider = styled.span`
  margin: 0 10px;
  color: #666;
`;
const ThemeToggle = styled.label`
  position: absolute;
  top: 20px;
  right: 20px;
  cursor: pointer;
`;
const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [theme, setTheme] = useState("light"); // État pour le thème
  const { login, error } = useAuth(); // Utiliser le contexte d'authentification
  const homenavigate = useNavigate();
  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    document.documentElement.setAttribute("data-theme", newTheme);
  };
  const handleLogin = async (e) => {
    e.preventDefault();
    const user = await login(username, password);
    if (user) {
      homenavigate("/home"); // Redirigez vers la page d'accueil après une connexion réussie
    }
  };
  return (
    <LoginContainer>
      <ThemeToggle className="swap swap-rotate">
        <input type="checkbox" onChange={toggleTheme} />
      </ThemeToggle>
      <Form onSubmit={handleLogin}>
        <LogoContainer>
          <LoginLogo
            src={logo}
            alt="Logo Galaxia"
            onClick={() => homenavigate("/home")}
          />
        </LogoContainer>
        <LoginTitle>Login</LoginTitle>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <InputContainer>
          <Input
            type="text"
            placeholder="Nom d'utilisateur ou Email"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </InputContainer>
        <InputContainer>
          <Input
            type="password"
            placeholder="Mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </InputContainer>
        <SubmitButton type="submit">Login</SubmitButton>
        <FormLinks>
          <Link href="/forgot_password">Mot de passe oublié ?</Link>
          <Divider>|</Divider>
          <Link href="/api/users/signup">Pas de compte ? Inscrivez-vous</Link>
        </FormLinks>
      </Form>
    </LoginContainer>
  );
};
export default Login;
