import React, { useState } from "react";
import { useGame } from "../context/GameContext";
import logo from "../image/logo_1.png";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Title,
  Input,
  Textarea,
  Select,
  Button,
  Logo,
} from "../styles/GameSubmissionForm_SC";

// Composant de formulaire de soumission de jeu
const GameSubmissionForm = () => {
  const { submitGame, error } = useGame(); // Utilisation du contexte GameContext
  const [gameData, setGameData] = useState({
    title: "",
    description: "",
    category: "",
    platform: "",
    downloadLink: "",
    image: null,
    zipFile: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setGameData({ ...gameData, [name]: value });
  };

  // Gère le changement de fichier pour l'image et le fichier zip
  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setGameData({ ...gameData, [name]: files[0] });
  };

  // Gère la soumission du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();

    // Ajout des champs de données au formulaire
    formData.append("title", gameData.title);
    formData.append("description", gameData.description);
    formData.append("category", gameData.category);
    formData.append("platform", gameData.platform);
    formData.append("downloadLink", gameData.downloadLink);
    if (gameData.image) formData.append("image", gameData.image);
    if (gameData.zipFile) formData.append("zipFileName", gameData.zipFile);

    // Appel de la fonction submitGame du contexte pour soumettre les données
    submitGame(formData);
  };

  const navigate = useNavigate();

  return (
    <Container>
      <Logo>
        <img src={logo} alt="Logo" onClick={() => navigate("/home")} />
      </Logo>
      <Title>Soumettre un Jeu</Title>
      <form onSubmit={handleSubmit}>
        <Input
          type="text"
          name="title"
          placeholder="Nom du jeu"
          value={gameData.title}
          onChange={handleChange}
          required
        />
        <Textarea
          name="description"
          placeholder="Description du jeu"
          rows="4"
          value={gameData.description}
          onChange={handleChange}
          required
        />
        <Select
          name="category"
          value={gameData.category}
          onChange={handleChange}
          required
        >
          <option value="">Sélectionnez une catégorie</option>
          <option value="action">Action</option>
          <option value="aventure">Aventure</option>
          <option value="puzzle">Puzzle</option>
          {/* Ajoutez d'autres catégories ici */}
        </Select>
        <Select
          name="platform"
          value={gameData.platform}
          onChange={handleChange}
          required
        >
          <option value="">Sélectionnez une plateforme</option>
          <option value="web">Web</option>
          {/* Ajoutez d'autres plateformes ici */}
        </Select>
        <Input
          type="url"
          name="downloadLink"
          placeholder="Lien de téléchargement"
          value={gameData.downloadLink}
          onChange={handleChange}
          required
        />
        <Input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleFileChange}
        />
        <Input
          type="file"
          name="zipFile"
          accept=".zip"
          onChange={handleFileChange}
          required
        />
        <Button type="submit">Soumettre</Button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </Container>
  );
};

export default GameSubmissionForm;
