import React, { useState } from "react";
import { useGame } from "../context/GameContext";
import logo from "../image/logo_1.png";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Title,
  Input,
  Textarea,
  Button,
  Logo,
  Select, // Ajout du Select importé
} from "../styles/GameSubmissionForm_SC";

const TAGS = [
  "Action",
  "Adventure",
  "Casual",
  "Co-op",
  "Cyberpunk",
  "Educational",
  "Fantasy",
  "Fighting",
  "Hardcore",
  "Historical",
  "Horror",
  "Kids",
  "Mature",
  "Multi-Player",
  "Mystery",
  "Open-World",
  "Platformer",
  "Post-Apocalyptic",
  "Racing",
  "RPG",
  "Roguelike",
  "Sandbox",
  "Shooter",
  "Single-Player",
  "Simulation",
  "Sports",
  "Stealth",
  "Survival",
  "Puzzle",
  "Western",
  "Zombie",
  "Pixel Art",
  "2D",
  "3D",
  "First-Person",
  "Third-Person",
];

const GameSubmissionForm = () => {
  const { submitGame, error } = useGame();
  const [gameData, setGameData] = useState({
    title: "",
    description: "",
    categories: [],
    platform: "",
    downloadLink: "",
    image: null,
    zipFile: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setGameData({ ...gameData, [name]: value });
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setGameData({ ...gameData, [name]: files[0] });
  };

  const handleCategoryChange = (tag) => {
    setGameData((prevState) => {
      const isSelected = prevState.categories.includes(tag);
      return {
        ...prevState,
        categories: isSelected
          ? prevState.categories.filter((category) => category !== tag)
          : [...prevState.categories, tag],
      };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", gameData.title);
    formData.append("description", gameData.description);
    formData.append("categories", JSON.stringify(gameData.categories));
    formData.append("platform", gameData.platform);
    formData.append("downloadLink", gameData.downloadLink);
    if (gameData.image) formData.append("image", gameData.image);
    if (gameData.zipFile) formData.append("zipFileName", gameData.zipFile);

    submitGame(formData);
  };

  const navigate = useNavigate();

  return (
    <Container>
      <Logo>
        <img src={logo} alt="Logo" onClick={() => navigate("/home")} />
      </Logo>
      <Title>Soumettre un Jeu</Title>
      <form onSubmit={handleSubmit}>
        <Input
          type="text"
          name="title"
          placeholder="Nom du jeu"
          value={gameData.title}
          onChange={handleChange}
          required
        />
        <Textarea
          name="description"
          placeholder="Description du jeu"
          rows="4"
          value={gameData.description}
          onChange={handleChange}
          required
        />

        <div>
          <p>Sélectionnez une ou plusieurs catégories :</p>
          {TAGS.map((tag) => (
            <label key={tag}>
              <input
                type="checkbox"
                value={tag}
                checked={gameData.categories.includes(tag)}
                onChange={() => handleCategoryChange(tag)}
              />
              {tag}
            </label>
          ))}
        </div>

        <Select
          name="platform"
          value={gameData.platform} // Utilisation de gameData.platform
          onChange={handleChange} // Utilisation de handleChange
          required
        >
          <option value="">Sélectionnez une plateforme</option>
          <option value="web">Web</option>
          <option value="pc">PC</option>
          {/* Ajoute d'autres plateformes ici */}
        </Select>

        <div>
          <p>Game Cover Art:</p>
          <Input
            type="file"
            name="image"
            accept="image/*"
            onChange={handleFileChange}
          />
        </div>
        <div></div>
        <Input
          type="file"
          name="zipFile"
          accept=".zip"
          onChange={handleFileChange}
          required
        />

        <Button type="submit">Soumettre</Button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </Container>
  );
};

export default GameSubmissionForm;
