import React, { useRef } from "react";
import { useGame } from "../context/GameContext";
import logo from "../image/logo_1.png";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Title,
  Input,
  Textarea,
  Button,
  Logo,
  Select,
  DropdownContainer,
  TagList,
  TagCheckbox,
  TagLabel,
} from "../styles/GameSubmissionForm_SC";

const TAGS = [
  "Action", "Adventure", "Casual", "Co-op", "Cyberpunk", "Educational",
  "Fantasy", "Fighting", "Hardcore", "Historical", "Horror", "Kids", "Mature",
  "Multi-Player", "Mystery", "Open-World", "Platformer", "Post-Apocalyptic",
  "Racing", "RPG", "Roguelike", "Sandbox", "Shooter", "Single-Player",
  "Simulation", "Sports", "Stealth", "Survival", "Puzzle", "Western", "Zombie",
  "Pixel Art", "2D", "3D", "First-Person", "Third-Person",
];

const GameSubmissionForm = () => {
  const { submitGame, error } = useGame();
  const navigate = useNavigate();
  const formDataRef = useRef({
    title: "",
    description: "",
    categories: [],
    platform: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    formDataRef.current[name] = value;
  };

  const handleCategoryChange = (tag) => {
    const categories = formDataRef.current.categories;
    if (categories.includes(tag)) {
      formDataRef.current.categories = categories.filter((t) => t !== tag);
    } else {
      formDataRef.current.categories.push(tag);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", formDataRef.current.title);
    formData.append("description", formDataRef.current.description);
    formData.append("categories", JSON.stringify(formDataRef.current.categories));
    formData.append("platform", formDataRef.current.platform);

    if (e.target.image.files[0]) {
      formData.append("image", e.target.image.files[0]);
    }
    if (e.target.zipFile.files[0]) {
      formData.append("zipFileName", e.target.zipFile.files[0]);
    }

    // Affiche tous les champs de FormData avant envoi
    console.log("FormData prêt à être envoyé :", Array.from(formData.entries()));
    submitGame(formData);
  };

  return (
    <Container>
      <Logo>
        <img src={logo} alt="Logo" onClick={() => navigate("/home")} />
      </Logo>
      <Title>Soumettre un Jeu</Title>
      <form onSubmit={handleSubmit}>
        <Input
          type="text"
          name="title"
          placeholder="Nom du jeu"
          onChange={handleChange}
          required
        />
        <Textarea
          name="description"
          placeholder="Description du jeu"
          rows="4"
          onChange={handleChange}
          required
        />

        <DropdownContainer>
          <Select onClick={() => (formDataRef.current.categoryDropdownOpen = !formDataRef.current.categoryDropdownOpen)}>
            {formDataRef.current.categories.length > 0
              ? `Catégories sélectionnées: ${formDataRef.current.categories.join(", ")}`
              : "Sélectionnez des catégories"}
          </Select>
          {formDataRef.current.categoryDropdownOpen && (
            <TagList>
              {TAGS.map((tag) => (
                <TagLabel key={tag}>
                  <TagCheckbox
                    type="checkbox"
                    checked={formDataRef.current.categories.includes(tag)}
                    onChange={() => handleCategoryChange(tag)}
                  />
                  {tag}
                </TagLabel>
              ))}
            </TagList>
          )}
        </DropdownContainer>

        <Select
          name="platform"
          onChange={handleChange}
          required
        >
          <option value="">Sélectionnez une plateforme</option>
          <option value="web">Web</option>
          <option value="pc">PC</option>
          {/* Ajoutez d'autres options ici */}
        </Select>

        <Input
          type="file"
          name="image"
          accept="image/*"
        />
        <Input
          type="file"
          name="zipFile"
          accept=".zip"
          // Retrait de l'attribut `required` pour rendre le fichier ZIP non obligatoire
        />

        <Button type="submit">Soumettre</Button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </Container>
  );
};

export default GameSubmissionForm;
