import React, { useState } from "react";
import { useGame } from "../context/GameContext";
import logo from "../image/logo_1.png";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Title,
  Input,
  Textarea,
  Button,
  Logo,
  Select,
  DropdownContainer,
  TagList,
  TagCheckbox,
  TagLabel,
} from "../styles/GameSubmissionForm_SC";

const TAGS = [
  "Action", "Adventure", "Casual", "Co-op", "Cyberpunk", "Educational",
  "Fantasy", "Fighting", "Hardcore", "Historical", "Horror", "Kids", "Mature",
  "Multi-Player", "Mystery", "Open-World", "Platformer", "Post-Apocalyptic",
  "Racing", "RPG", "Roguelike", "Sandbox", "Shooter", "Single-Player",
  "Simulation", "Sports", "Stealth", "Survival", "Puzzle", "Western", "Zombie",
  "Pixel Art", "2D", "3D", "First-Person", "Third-Person",
];

// Fonction utilitaire pour afficher le contenu de FormData
const logFormData = (formData) => {
  for (let [key, value] of formData.entries()) {
    console.log(`${key}:`, value);
  }
};


const GameSubmissionForm = () => {
  const { submitGame, error } = useGame();
  const navigate = useNavigate();
  const [gameData, setGameData] = useState({
    title: "",
    description: "",
    categories: [],
    platform: "",
    downloadLink: "",
    image: null,
    zipFile: null,
  });
  const [categoryDropdownOpen, setCategoryDropdownOpen] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setGameData({ ...gameData, [name]: value });
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setGameData({ ...gameData, [name]: files[0] });
  };

  const handleCategoryChange = (tag) => {
    setGameData((prevState) => {
      const isSelected = prevState.categories.includes(tag);
      return {
        ...prevState,
        categories: isSelected
          ? prevState.categories.filter((category) => category !== tag)
          : [...prevState.categories, tag],
      };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", gameData.title);
    console.log("GameSubmission - title :", gameData.title);

    formData.append("description", gameData.description);
    console.log("GameSubmission - description :", gameData.description);

    formData.append("categories", JSON.stringify(gameData.categories));
    console.log("GameSubmission - categories :", JSON.stringify(gameData.categories));

    formData.append("platform", gameData.platform);
    console.log("GameSubmission - platform :", gameData.platform);

    if (gameData.image) {
        formData.append("image", gameData.image);
        console.log("GameSubmission - image :", gameData.image);
    } else {
        console.log("GameSubmission - Pas d'image sélectionnée");
    }

    if (gameData.zipFile) {
        formData.append("zipFileName", gameData.zipFile);
        console.log("GameSubmission - zipFileName :", gameData.zipFile);
    } else {
        console.log("GameSubmission - Pas de fichier ZIP sélectionné");
    }

    // Affiche tous les champs de FormData avant envoi
    console.log("GameSubmission - FormData prêt à être envoyé :", Array.from(formData.entries()));


    // Soumet les données
    submitGame(formData);

    // Confirme l'envoi
    console.log("GameSubmission - FormData envoyé :", Array.from(formData.entries()));

};


  return (
    <Container>
      <Logo>
        <img src={logo} alt="Logo" onClick={() => navigate("/home")} />
      </Logo>
      <Title>Soumettre un Jeu</Title>
      <form onSubmit={handleSubmit}>
        <Input
          type="text"
          name="title"
          placeholder="Nom du jeu"
          value={gameData.title}
          onChange={handleChange}
          required
        />
        <Textarea
          name="description"
          placeholder="Description du jeu"
          rows="4"
          value={gameData.description}
          onChange={handleChange}
          required
        />

        <DropdownContainer>
          <Select onClick={() => setCategoryDropdownOpen(!categoryDropdownOpen)}>
            {gameData.categories.length > 0
              ? `Catégories sélectionnées: ${gameData.categories.join(", ")}`
              : "Sélectionnez des catégories"}
          </Select>
          {categoryDropdownOpen && (
            <TagList>
              {TAGS.map((tag) => (
                <TagLabel key={tag}>
                  <TagCheckbox
                    type="checkbox"
                    checked={gameData.categories.includes(tag)}
                    onChange={() => handleCategoryChange(tag)}
                  />
                  {tag}
                </TagLabel>
              ))}
            </TagList>
          )}
        </DropdownContainer>

        <Select
          name="platform"
          value={gameData.platform}
          onChange={handleChange}
          required
        >
          <option value="">Sélectionnez une plateforme</option>
          <option value="web">Web</option>
          <option value="pc">PC</option>
          {/* Ajoutez d'autres options ici */}
        </Select>

        <Input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleFileChange}
        />
        <Input
          type="file"
          name="zipFile"
          accept=".zip"
          onChange={handleFileChange}
          required
        />

        <Button type="submit">Soumettre</Button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </Container>
  );
};

export default GameSubmissionForm;
