import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TagDropdown from './FuncTagDropdown';

const GameSubmissionForm = () => {
  const [selectedTags, setSelectedTags] = useState([]);
  const [tagOptions, setTagOptions] = useState([]);

  // Récupère les tags depuis le backend au chargement du composant
  useEffect(() => {
    const fetchTags = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/tags/all"); // Modifiez l'URL si nécessaire
        setTagOptions(response.data.map(tag => tag.name)); // On utilise seulement les noms
      } catch (error) {
        console.error("Erreur lors de la récupération des tags :", error);
      }
    };

    fetchTags();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Tags sélectionnés:', selectedTags);
    // Traitez ici l'enregistrement des tags avec les autres données du formulaire
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Enregistrer un jeu</h2>
      <TagDropdown
        options={tagOptions}
        selectedTags={selectedTags}
        setSelectedTags={setSelectedTags}
      />
      <button type="submit">Enregistrer</button>
    </form>
  );
};

export default GameSubmissionForm;
