import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import logo from "../image/logo_1.png";

// Composants stylisés
const AppContainer = styled.div`
  background-color: var(--bg-color);
  color: var(--text-color);
`;

const Header = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  background-color: var(--bg-color);
`;

const Logo = styled.div`
  font-size: 24px;
  font-weight: bold;
  margin-left: 40px;
  img {
    width: 150px;
    height: auto;
  }
`;

const NavButton = styled.button`
  margin: 0 10px;
  padding: 10px 20px;
  background-color: #eaf0f7;
  color: rgb(0, 0, 0);
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const UserActions = styled.div`
  display: flex;
  align-items: center;
  button {
    margin: 0 10px;
    padding: 10px 20px;
    background-color: #ffffff;
    color: rgb(0, 0, 0);
    border: none;
    border-radius: 5px;
    cursor: pointer;
  }
  select {
    margin-left: 10px;
    padding: 10px;
  }
`;

const Banner = styled.div`
  height: 200px;
  background-color: #ddd;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Filters = styled.div`
  display: flex;
  justify-content: center;
  padding: 10px;
  background-color: #f9f9f9;
  select,
  input {
    margin: 0 10px;
    padding: 10px;
  }
`;

const MainContent = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 20px;
`;

const Sidebar = styled.div`
  width: 20%;
  background-color: #f0f0f0;
  .ad,
  .event {
    margin: 20px;
    background-color: #ccc;
    padding: 20px;
    text-align: center;
  }
`;

const GamesList = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
  width: 60%;
  margin-top: 20px;
  margin-left: 20px;
  margin-right: 20px;
`;

const Game = styled.div`
  background-color: #fff;
  border: 1px solid #ddd;
  padding: 20px;
  text-align: center;
  margin-bottom: 30px;
  .game-image {
    background-color: #ccc;
    height: 170px;
    margin-bottom: 10px;
  }
`;

const Footer = styled.footer`
  padding: 26px;
  text-align: center;
  background-color: #f1f1f1;
  position: relative;
  bottom: 0;
  width: 100%;
`;

// Liste des jeux (exemple)
const gameData = [
  {
    id: 1,
    name: "Jeu 1",
    image: "URL_IMAGE_JEU_1", // Remplacez par l'URL de l'image
    rating: "4.5",
  },
  {
    id: 2,
    name: "Jeu 2",
    image: "URL_IMAGE_JEU_2", // Remplacez par l'URL de l'image
    rating: "4.0",
  },
  {
    id: 3,
    name: "Jeu 3",
    image: "URL_IMAGE_JEU_3", // Remplacez par l'URL de l'image
    rating: "5.0",
  },
  // Ajoutez d'autres jeux ici...
];

function Home() {
  const [theme, setTheme] = useState("light");
  const toggleTheme = () => {
    const newTheme = theme === "light" ? "dark" : "light";
    setTheme(newTheme);
    document.documentElement.setAttribute("data-theme", newTheme);
  };

  const navigate = useNavigate();

  return (
    <AppContainer className={`App ${theme}`}>
      <Header>
        <Logo>
          <img src={logo} alt="Logo" />
        </Logo>
        <nav>
          <NavButton onClick={() => navigate("/home")}>Magasin</NavButton>
          <NavButton onClick={() => navigate("/Bibliothèque")}>
            Bibliothèque
          </NavButton>
          <NavButton onClick={() => navigate("/user")}>User</NavButton>
        </nav>
        <UserActions>
          <button onClick={() => navigate("/login")}>Login</button>
          <button onClick={() => navigate("/api/users/signup")}>Sign Up</button>
          <select>
            <option>Langue</option>
          </select>
          <label className="swap swap-rotate">
            <input type="checkbox" onChange={toggleTheme} />
            <svg
              className="swap-off"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
            ></svg>
            <svg
              className="swap-on"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 24 24"
            ></svg>
          </label>
        </UserActions>
      </Header>
      <Banner>
        <div className="banner-content">Image ici</div>
      </Banner>
      <Filters>
        <select>
          <option>Genre</option>
        </select>
        <select>
          <option>Prix</option>
        </select>
        <select>
          <option>Game Langue</option>
        </select>
        <select>
          <option>Note</option>
        </select>
        <select>
          <option>Date de sortie</option>
        </select>
        <input type="text" placeholder="Recherche..." />
      </Filters>
      <MainContent>
        <Sidebar>
          <div className="ad">Pub</div>
        </Sidebar>
        <GamesList>
          {gameData.map((game) => (
            <Game key={game.id}>
              <NavButton
                className="game-image"
                onClick={() => navigate("/game")}
              >
                <img src={game.image} alt={game.name} />
              </NavButton>
              <div className="game-info">
                <p>{game.name}</p>
                <p>Note: {game.rating}</p>
              </div>
            </Game>
          ))}
        </GamesList>
        <Sidebar>
          <div className="event">Événement</div>
          <div className="event">Événement</div>
        </Sidebar>
      </MainContent>
      <Footer>
        <p>Texte du pied de page ou mentions légales ici.</p>
      </Footer>
    </AppContainer>
  );
}

export default Home;
