import React, { useState, useEffect, useRef } from "react";
import logo from "../image/logo_1.png";
import { useNavigate } from "react-router-dom";
import {
  Container,
  Title,
  Input,
  Textarea,
  Button,
  Logo,
} from "../styles/GameSubmissionForm_SC";
import axios from "axios";
import styled from "styled-components";

const DropdownContainer = styled.div`
  position: relative;
  margin-bottom: 1rem;
`;

const DropdownButton = styled.div`
  padding: 10px;
  border: 1px solid #ccc;
  cursor: pointer;
  background-color: #f8f8f8;
`;

const DropdownMenu = styled.div`
  position: absolute;
  top: 100%;
  left: 0;
  background: white;
  border: 1px solid #ccc;
  max-height: 200px;
  overflow-y: auto;
  z-index: 10;
`;

const TagItem = styled.label`
  display: block;
  padding: 5px 10px;
  cursor: pointer;
  input {
    margin-right: 8px;
  }
`;

const GameSubmissionForm = () => {
  const [gameData, setGameData] = useState({
    name: "",
    developerName: "",
    description: "",
    platform: "",
    gameEngine: "",
    tags: [],
  });
  const [zipFileName, setZipFile] = useState(null);
  const [availableTags, setAvailableTags] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [error, setError] = useState(null);
  const dropdownRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTags = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/tags");
        setAvailableTags(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération des tags:", error);
      }
    };
    fetchTags();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setGameData({ ...gameData, [name]: value });
  };

  const handleFileChange = (e) => {
    setZipFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = sessionStorage.getItem("token");
    if (!token) {
      setError("Vous devez être connecté pour soumettre un jeu.");
      return;
    }
    const formData = new FormData();
    formData.append("name", gameData.name);
    formData.append("developerName", gameData.developerName);
    formData.append("description", gameData.description);
    formData.append("platform", gameData.platform);
    formData.append("gameEngine", gameData.gameEngine);
    gameData.tags.forEach((tag) => formData.append("tags", tag));
    formData.append("zipFile", zipFileName);
    try {
      const response = await axios.post(
        "http://localhost:5000/api/Game/submit",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      if (response.status === 201) {
        navigate("/games");
      }
    } catch (error) {
      setError(
        error.response
          ? error.response.data.message
          : "Erreur lors de la soumission du jeu"
      );
    }
  };

  const toggleDropdown = () => {
    setIsDropdownOpen((prev) => !prev);
  };

  const handleTagChange = (tagId) => {
    setGameData((prevData) => {
      const tags = prevData.tags.includes(tagId)
        ? prevData.tags.filter((tag) => tag !== tagId)
        : [...prevData.tags, tagId];
      return { ...prevData, tags };
    });
  };

  const closeDropdown = (e) => {
    if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
      setIsDropdownOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", closeDropdown);
    return () => {
      document.removeEventListener("mousedown", closeDropdown);
    };
  }, []);

  return (
    <Container>
      <Logo>
        <img src={logo} alt="Logo" onClick={() => navigate("/home")} />
      </Logo>
      <Title>Soumettre un Jeu</Title>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <Input
          type="text"
          name="name"
          placeholder="Nom du jeu"
          value={gameData.name}
          onChange={handleChange}
          required
        />
        <Input
          type="text"
          name="developerName"
          placeholder="Nom du développeur"
          value={gameData.developerName}
          onChange={handleChange}
          required
        />
        <Textarea
          name="description"
          placeholder="Description du jeu"
          rows="4"
          value={gameData.description}
          onChange={handleChange}
          required
        />
        <Input
          type="text"
          name="platform"
          placeholder="Plateforme"
          value={gameData.platform}
          onChange={handleChange}
          required
        />
        <Input
          type="text"
          name="gameEngine"
          placeholder="Moteur de jeu"
          value={gameData.gameEngine}
          onChange={handleChange}
          required
        />

        {/* Dropdown pour les tags */}
        <DropdownContainer ref={dropdownRef}>
          <DropdownButton onClick={toggleDropdown}>
            {gameData.tags.length > 0
              ? `${gameData.tags.length} tags sélectionnés`
              : "Sélectionnez des tags"}
          </DropdownButton>
          {isDropdownOpen && (
            <DropdownMenu>
              {availableTags.map((tag) => (
                <TagItem key={tag.id}>
                  <input
                    type="checkbox"
                    checked={gameData.tags.includes(tag.id)}
                    onChange={() => handleTagChange(tag.id)}
                  />
                  {tag.name}
                </TagItem>
              ))}
            </DropdownMenu>
          )}
        </DropdownContainer>

        <Input
          type="file"
          name="zipFile"
          accept=".zip"
          onChange={handleFileChange}
          required
        />
        {error && <p style={{ color: "red" }}>{error}</p>}
        <Button type="submit">Soumettre</Button>
      </form>
    </Container>
  );
};

export default GameSubmissionForm;
